{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "trend_fetcher Input Schema",
  "type": "object",
  "required": ["skill_name", "parameters"],
  "properties": {
    "skill_name": {
      "type": "string",
      "const": "trend_fetcher",
      "description": "Skill identifier"
    },
    "parameters": {
      "type": "object",
      "required": ["region", "category"],
      "properties": {
        "region": {
          "type": "string",
          "description": "Geographic region for trend analysis (e.g., 'ethiopia', 'global')"
        },
        "category": {
          "type": "string",
          "description": "Category of trends (e.g., 'fashion', 'technology', 'crypto')"
        },
        "timeframe_hours": {
          "type": "integer",
          "minimum": 1,
          "maximum": 168,
          "default": 24,
          "description": "Time window for trend analysis in hours (default: 24)"
        },
        "relevance_threshold": {
          "type": "number",
          "minimum": 0.0,
          "maximum": 1.0,
          "default": 0.75,
          "description": "Minimum relevance score for trends to be included (default: 0.75)"
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "trend_fetcher Output Schema",
  "type": "object",
  "required": ["trends", "metadata"],
  "properties": {
    "trends": {
      "type": "array",
      "items": {
        "type": "object",
        "required": ["topic", "engagement_score", "relevance_score"],
        "properties": {
          "topic": {
            "type": "string",
            "description": "Trend topic name"
          },
          "engagement_score": {
            "type": "number",
            "minimum": 0.0,
            "maximum": 1.0,
            "description": "Engagement metric (0.0-1.0)"
          },
          "growth_rate": {
            "type": "string",
            "description": "Growth rate indicator (e.g., '+15%', '-5%')"
          },
          "sources": {
            "type": "array",
            "items": {
              "type": "string",
              "pattern": "^mcp://"
            },
            "description": "MCP Resource URIs that contributed to this trend"
          },
          "relevance_score": {
            "type": "number",
            "minimum": 0.0,
            "maximum": 1.0,
            "description": "Relevance score for the requesting agent (0.0-1.0)"
          }
        }
      },
      "description": "List of trending topics sorted by relevance_score (descending)"
    },
    "metadata": {
      "type": "object",
      "required": ["fetched_at", "source_count", "confidence"],
      "properties": {
        "fetched_at": {
          "type": "string",
          "format": "date-time",
          "description": "ISO 8601 timestamp when trends were fetched"
        },
        "source_count": {
          "type": "integer",
          "minimum": 0,
          "description": "Number of MCP Resources queried"
        },
        "confidence": {
          "type": "number",
          "minimum": 0.0,
          "maximum": 1.0,
          "description": "Overall confidence in trend data quality (0.0-1.0)"
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "content_generator Input Schema",
  "type": "object",
  "required": ["skill_name", "parameters"],
  "properties": {
    "skill_name": {
      "type": "string",
      "const": "content_generator",
      "description": "Skill identifier"
    },
    "parameters": {
      "type": "object",
      "required": ["content_type", "platform", "topic"],
      "properties": {
        "content_type": {
          "type": "string",
          "enum": ["text", "image", "video", "multimodal"],
          "description": "Type of content to generate"
        },
        "platform": {
          "type": "string",
          "enum": ["twitter", "instagram", "threads", "tiktok"],
          "description": "Target social media platform"
        },
        "topic": {
          "type": "string",
          "description": "Topic or theme for content generation"
        },
        "persona_constraints": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "List of persona constraints from SOUL.md (e.g., voice traits, directives)"
        },
        "character_reference_id": {
          "type": "string",
          "description": "Character LoRA identifier for image/video consistency (required for image/video content)"
        },
        "tier": {
          "type": "string",
          "enum": ["daily", "hero"],
          "default": "daily",
          "description": "Content tier: 'daily' uses Image-to-Video, 'hero' uses Text-to-Video"
        },
        "budget_limit_usdc": {
          "type": "number",
          "minimum": 0.0,
          "description": "Maximum budget allocation for content generation (USDC)"
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "content_generator Output Schema",
  "type": "object",
  "required": ["content", "metadata"],
  "properties": {
    "content": {
      "type": "object",
      "required": ["text", "platform", "disclosure_level"],
      "properties": {
        "text": {
          "type": "string",
          "description": "Generated caption/post text",
          "maxLength": 280
        },
        "image_url": {
          "type": ["string", "null"],
          "format": "uri",
          "description": "URL of generated image (if applicable)"
        },
        "video_url": {
          "type": ["string", "null"],
          "format": "uri",
          "description": "URL of generated video (if applicable)"
        },
        "platform": {
          "type": "string",
          "enum": ["twitter", "instagram", "threads", "tiktok"],
          "description": "Target platform"
        },
        "disclosure_level": {
          "type": "string",
          "enum": ["automated", "assisted", "none"],
          "description": "AI disclosure level per platform requirements"
        }
      }
    },
    "metadata": {
      "type": "object",
      "required": ["generated_at", "generation_cost_usdc", "character_consistency_score", "brand_alignment_score"],
      "properties": {
        "generated_at": {
          "type": "string",
          "format": "date-time",
          "description": "ISO 8601 timestamp when content was generated"
        },
        "generation_cost_usdc": {
          "type": "number",
          "minimum": 0.0,
          "description": "Cost of content generation in USDC"
        },
        "character_consistency_score": {
          "type": "number",
          "minimum": 0.0,
          "maximum": 1.0,
          "description": "Character consistency score (0.0-1.0), validated by Judge"
        },
        "brand_alignment_score": {
          "type": "number",
          "minimum": 0.0,
          "maximum": 1.0,
          "description": "Brand alignment score based on persona constraints (0.0-1.0)"
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "engagement_manager Input Schema",
  "type": "object",
  "required": ["skill_name", "parameters"],
  "properties": {
    "skill_name": {
      "type": "string",
      "const": "engagement_manager",
      "description": "Skill identifier"
    },
    "parameters": {
      "type": "object",
      "required": ["interaction_type", "platform", "target_content_id", "target_user_id", "persona_constraints"],
      "properties": {
        "interaction_type": {
          "type": "string",
          "enum": ["reply", "comment", "dm", "like"],
          "description": "Type of social media interaction"
        },
        "platform": {
          "type": "string",
          "enum": ["twitter", "instagram", "threads"],
          "description": "Social media platform"
        },
        "target_content_id": {
          "type": "string",
          "description": "ID of the content being interacted with (e.g., tweet ID, post ID)"
        },
        "target_user_id": {
          "type": "string",
          "description": "ID of the user being interacted with"
        },
        "context": {
          "type": "object",
          "properties": {
            "conversation_history": {
              "type": "array",
              "items": {
                "type": "string"
              },
              "description": "Recent conversation messages"
            },
            "user_profile": {
              "type": "object",
              "properties": {
                "followers": {"type": "integer"},
                "verified": {"type": "boolean"},
                "interests": {
                  "type": "array",
                  "items": {"type": "string"}
                }
              },
              "description": "User profile information"
            },
            "trending_topics": {
              "type": "array",
              "items": {"type": "string"},
              "description": "Current trending topics for context"
            }
          },
          "description": "Contextual information for response generation"
        },
        "persona_constraints": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "List of persona constraints from SOUL.md (voice traits, directives, beliefs)"
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "engagement_manager Output Schema",
  "type": "object",
  "required": ["interaction", "metadata"],
  "properties": {
    "interaction": {
      "type": "object",
      "required": ["type", "platform", "response_text", "sent_at"],
      "properties": {
        "type": {
          "type": "string",
          "enum": ["reply", "comment", "dm", "like"],
          "description": "Type of interaction"
        },
        "platform": {
          "type": "string",
          "enum": ["twitter", "instagram", "threads"],
          "description": "Platform where interaction occurred"
        },
        "response_text": {
          "type": "string",
          "description": "Generated response text"
        },
        "sent_at": {
          "type": "string",
          "format": "date-time",
          "description": "ISO 8601 timestamp when interaction was sent"
        }
      }
    },
    "metadata": {
      "type": "object",
      "required": ["context_used", "relevance_score", "sentiment_score"],
      "properties": {
        "context_used": {
          "type": "object",
          "required": ["episodic_memory_count", "semantic_memory_count", "soul_md_sections"],
          "properties": {
            "episodic_memory_count": {
              "type": "integer",
              "minimum": 0,
              "description": "Number of episodic memories (Redis) used"
            },
            "semantic_memory_count": {
              "type": "integer",
              "minimum": 0,
              "description": "Number of semantic memories (Weaviate) used"
            },
            "soul_md_sections": {
              "type": "array",
              "items": {
                "type": "string",
                "enum": ["backstory", "voice_traits", "core_beliefs", "directives"]
              },
              "description": "SOUL.md sections referenced in response generation"
            }
          }
        },
        "relevance_score": {
          "type": "number",
          "minimum": 0.0,
          "maximum": 1.0,
          "description": "Relevance score of response to context (0.0-1.0)"
        },
        "sentiment_score": {
          "type": "number",
          "minimum": -1.0,
          "maximum": 1.0,
          "description": "Sentiment score of response (-1.0 negative to 1.0 positive)"
        }
      }
    }
  }
}
